-- Create "attribute_reports" table
CREATE TABLE `attribute_reports` (`id` bigint NOT NULL AUTO_INCREMENT, `statement_attributes_report` bigint NOT NULL, PRIMARY KEY (`id`), UNIQUE INDEX `statement_attributes_report` (`statement_attributes_report`), CONSTRAINT `attribute_reports_statements_attributes_report` FOREIGN KEY (`statement_attributes_report`) REFERENCES `statements` (`id`) ON UPDATE NO ACTION ON DELETE NO ACTION) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "attribute_assertions" table
CREATE TABLE `attribute_assertions` (`id` bigint NOT NULL AUTO_INCREMENT, `attribute` varchar(255) NOT NULL, `target` json NULL, `conditions` json NULL, `evidence` json NULL, `attribute_report_attributes` bigint NULL, PRIMARY KEY (`id`), UNIQUE INDEX `attribute` (`attribute`), INDEX `attribute_assertions_attribute_reports_attributes` (`attribute_report_attributes`), CONSTRAINT `attribute_assertions_attribute_reports_attributes` FOREIGN KEY (`attribute_report_attributes`) REFERENCES `attribute_reports` (`id`) ON UPDATE NO ACTION ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
